mapper.select:
select aptCode, buildYear, roadName, roadNameBonbun, roadNameBubun, apartmentName, lng, lat
from houseinfo
where dongCode = #{addrCode}

select avg(hd.dealAmount)
from houseinfo hi, housedeal hd
where hi.aptCode = hd.aptCode and hi.dongCode = #{addrCode}

select avg(hd.dealAmount)
from houseinfo hi, housedeal hd
where hi.aptCode = hd.aptCode and hi.sigunguCode = #{addrCode}
