mapper.select:
select question_no as no, user_id as userId, title, content, create_time as createTime, update_time as updateTime, hit from questions
where title like concat('%', #{word}, '%')
order by question_no desc limit #{start}, #{spp}

select question_no as `no`, user_id as userId, title,
content, create_time as createTime, update_time as updateTime, hit
from questions where question_no = #{no}

mapper.update:
update questions
title = #{title}, content = #{content}, update_time = CURRENT_TIMESTAMP,
where question_no = #{no}
