mapper.update:
update users
user_pw = #{pw}, user_name = #{name}, address = #{address}, phone = #{phone}, role = #{role},
where user_id=#{id}

mapper.select:
select token
from users
where user_id = #{userId}

mapper.update:
update users
set token = #{token}
where user_id = #{userId}

update users
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userId}
